import './App.css';
import {useState} from 'react';
import Axios from 'axios';

function App() {

  const [productName, setProductName] = useState('')
  const [productPrice, setProductPrice] = useState(0)

  const [productsList, setProductList] = useState([])

  const displayInfo = () => {
    console.log(productName, productPrice)
  }

  const addProduct = () => {
    Axios.post('http://localhost:3001/create', { product_name: productName, product_price: productPrice}).then(() => {
      console.log("Success")
    })
  }

  const employeeList = () => {
    Axios.get('http://localhost:3001/products').then((res) => {
      setProductList(res.data)  
      console.log(res)
    })
  }

  return (
    <div className='App'>
      <div className='input'>
        <label>Product Name:</label>
        <input type='text' onChange={(event) => {setProductName(event.target.value)}}/>
        <label>Product Price:</label>
        <input type='number' onChange={(event) => {setProductPrice(event.target.value)}}/>
        <button onClick={addProduct}>Add Product</button>
      </div>
      <div className='output'>
        <button onClick={employeeList}>Show Products</button>
      </div>
      <div className='products'>
        {productsList.map((val, key) => {
          return <div>{val.product_name}{val.productPrice}</div>;
        })}
      </div>
    </div>
  );
}

export default App;
